import Sell1 from "../assects/items/love u pendant.jpg";
import Sell2 from "../assects/items/My Tiny Heart Gold Earrings.jpg";
import Sell3 from "../assects/items/Diamond Dainty Ring.jpg";
import DestinationData from "./DestinationData";
import "./DestinationStyles.css";

const Destination = () => {
    return (
        <div className="destination">
            <h1>Popular Destination</h1>
            <p>Tours give you the opportunity to see a lot within a time frame.</p>

            {/* Call paragraph from the DestinationData.js */}
            <DestinationData
                // Reverse the position using CSS
                className="first-des"
                heading="Compliance Auditing"
                text="Some organiations like healthcare, defence and financial has specific industrial rules, regulations and standards. So
                in compliance auditing we ensure that organizations are follows those industral rules, regulations and standards."

                img1={Sell1}
                img2={Sell2}
            />

                <DestinationData
                // Reverse the position using CSS
                className="first-des-reverse"
                heading="Internal Auditing"
                text="Internal auditing ensure that organizations internal process in efficient, effective and good. An internal auditing is help
                organizations to improve security, accuracy and efficiency."

                img1={Sell1}
                img2={Sell3}
            />

            
            
        </div>
    );

};

export default Destination
