import "./DestinationStyles.css";

const Destination = () => {
    return (
        <div className="destination">

            {/* Call paragraph from the DestinationData.js */}
            <DestinationData
                // Reverse the position using CSS
                className="first-des"
                heading="Monarch of the gold World"
                text="NLM Jewellers counts over nine decades of successful enterprise as one of Sri Lankaâ€™s most accomplished entities in the gold jewellery industry."

                img1={img1}

            />

                <DestinationData
                // Reverse the position using CSS
                className="first-des-reverse"
                heading="AWARDS"
                text="Presidential and National business excellence award winner"

                img1={img1}

            />
            <DestinationData
                // Reverse the position using CSS
                className="first-des"
                heading="FAST AND SECURE SHIPPING"
                text="Fully secured online payment facility, order management process and International shipping"

                img1={img1}

            />
            
            
        </div>
    );

};

export default Destination
