import React, { useState } from 'react';
import './loginStyles.css'; // Import the CSS file

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState(''); // For error handling

  const handleSubmit = async (event) => {
    event.preventDefault(); // Prevent default form submission behavior

    try {
      // Simulate a login request to a backend API (replace with your actual logic)
      const response = await fetch('/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();

      if (data.success) {
        // Login successful! Handle navigation or other actions here
        console.log('Login successful! Redirecting...');
      } else {
        setErrorMessage(data.message || 'Login failed'); // Display error message
      }
    } catch (error) {
      // Handle network or general errors
      setErrorMessage('Something went wrong. Please try again.');
      console.error(error);
    }
  };

  return (
    <div className="login-container">
      <h1>Welcome Back!</h1>
      <form id="login-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            name="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {errorMessage && <p className="error-message">{errorMessage}</p>}
        <div className="form-actions">
          <button type="submit">Login</button>
          <a href="#">Forgot Password?</a>
        </div>
        <p>Don't have an account? <a href="#">Sign Up Here</a></p>
      </form>
    </div>
  );
}

export default Login;
